# Function to calculate the number of divisors
def number_of_divisors(n):
    factors = {}
    # Count the number of 2s that divide n
    while n % 2 == 0:
        if 2 in factors:
            factors[2] += 1
        else:
            factors[2] = 1
        n = n // 2
    # n must be odd at this point, so a skip of 2 (i.e., i = i + 2) can be used
    for i in range(3, int(n**0.5) + 1, 2):
        # While i divides n, count i and divide n
        while n % i == 0:
            if i in factors:
                factors[i] += 1
            else:
                factors[i] = 1
            n = n // i
    # Condition if n is a prime number greater than 2
    if n > 2:
        factors[n] = 1
    # Calculate the number of divisors
    num_divisors = 1
    for exponent in factors.values():
        num_divisors *= (exponent + 1)
    return num_divisors

# Function to calculate the sum of divisors
def sum_of_divisors(n):
    factors = {}
    # Count the number of 2s that divide n
    while n % 2 == 0:
        if 2 in factors:
            factors[2] += 1
        else:
            factors[2] = 1
        n = n // 2
    # n must be odd at this point, so a skip of 2 (i.e., i = i + 2) can be used
    for i in range(3, int(n**0.5) + 1, 2):
        # While i divides n, count i and divide n
        while n % i == 0:
            if i in factors:
                factors[i] += 1
            else:
                factors[i] = 1
            n = n // i
    # Condition if n is a prime number greater than 2
    if n > 2:
        factors[n] = 1
    # Calculate the sum of divisors
    sum_divisors = 1
    for prime, exponent in factors.items():
        sum_divisors *= (prime**(exponent + 1) - 1) // (prime - 1)
    return sum_divisors

